using System.Collections;using System.Collections.Generic;using UnityEngine;public class Movement : MonoBehaviour{public float speed,dashCooldown;private Vector2 movement2;void Update(){movement2.x=Input.GetAxisRaw("Horizontal");movement2.y=Input.GetAxisRaw("Vertical");movement2.Normalize();GetComponent<Rigidbody2D>().velocity=movement2*speed;}private IEnumerator Dash(){yield return new WaitForSeconds(dashCooldown);}}